        .send({
            from: accounts[0]
        });
    };
    render(){
        const {id, question} = this.props;
        const items= {
            header: question[0],
                description: (
                    <div>
                        <Button onClick={this.voteYes}>Yay</Button>
                        <Button>Nay</Button>
                    </div>
                )
        };
        return <Card.Group centered itemsPerRow={2} items ={items}/> ;
    }
}          
