import React, {Component} from 'react';
import Layout from '../components/Layout';
import {Button} from 'semantic-ui-react';
import poll from '../ethereum/poll';
import Question from '../components/Question';

class OngoingPoll extends Component{
    state={
        questionLength: '',
        question: []
    };

    async componentDidMount(){
        const questionLength = await poll.methods.getQuestionLength().call();

        const questions = await Promise.all(
            Array(parseInt(questionLength))
            .fill()
            .map((element, index) => {
                return poll.methods.questions(index).call()
            })
        );

        let question=[]
        var i;
        for (i=0;i<questionLength;i++){
        question[i]=questions[i];
        }


        this.setState({
            questionLength,
            question
        });

    }
    renderQuestion(){
        var i;
        for(i=0;i<this.state.questionLength;i++){
            var q=this.state.question[i];
            console.log(q);
        }

    return (
        <div>Hi</div>
        );
    }


    render(){
        return(
            <Layout>
                {this.state.questionLength}     
                {this.renderQuestion()}
                    <Button primary>Ask a question</Button>

            </Layout>
        );
    }
}

export default OngoingPoll;
