import React, {Component} from 'react';
import Layout from '../components/Layout';
import {Button, Card, Container} from 'semantic-ui-react';
import poll from '../ethereum/poll';
import {Link} from '../routes'
import web3 from '../ethereum/web3';

class OngoingPoll extends Component{
    static async getInitialProps(){
        const questionLength = await poll.methods.getQuestionLength().call();

        const questions = await Promise.all(
            Array(parseInt(questionLength))
            .fill()
            .map((element, index) => {
                return poll.methods.questions(index).call()
            })
        );
        console.log(questions);
        return{
            questionLength,
            questions
        }
    }

    voteYes = async () => {
        const accounts = await web3.eth.getAccounts();
        console.log('voted');
        await poll.methods.voteYay(0).send({
            from: accounts[0]
        });  
    };


    renderQuestion(){
        const items= this.props.questions.map((question, index) => {
            return{
                id: {index},
                header: question[0],
                description: (
                    <div>
                        <Button onClick={this.voteYes}>Yay</Button>
                        <Button>Nay</Button>
                    </div>
                )
            };
        });

    return <Card.Group centered itemsPerRow={2} items ={items}/> ;
    }



    voteNay(){

    }



    render(){
        return(
            <Layout>
                <Container>
                    <p>{this.props.questionLength} Ongoing Polls</p>
                    {this.renderQuestion()}       
                </Container>
                <Link route='/ongoing/add'>
                <Button primary style={{marginTop: '10px'}}>Ask a question</Button>
                </Link>

            </Layout>
        );
    }
}

export default OngoingPoll;
