import React, {Component} from 'react';
import poll from '../ethereum/poll';
import { Card, Button, Container, Item, Header} from 'semantic-ui-react';
import Layout from '../components/Layout';
import {Link} from '../routes';

class PollIndex extends Component {
    static async getInitialProps() {
        const manager = await poll.methods.manager().call();
        console.log(manager);
        return {manager};

    }

    render(){
        const manager = this.props.address;
        return (
            <Layout>
                <Header
                    as='h3'
                    content='This poll is managed by'
                    inverted
                    style={{
                        fontSize: '4em',
                        fontWeight: 'normal',
                        marginBottom: 0,
                        marginTop: '3em',
                        }}
                />
                <Header
                    as='h3'
                    content={manager}
                    inverted
                    style={{
                        fontSize: '1.7em',
                        fontWeight: 'normal',
                        marginTop: '1.5em',
                        }}
                />
                <Link route="/ongoing">
                <Button primary size='huge'>
                        View Poll
                </Button>
                </Link>

            </Layout>
        );
    }
}

export default PollIndex;
