import React, {Component} from 'react';
import Layout from '../components/Layout';
import {Button, Card} from 'semantic-ui-react';
import poll from '../ethereum/poll';
import Question from '../components/Question';

class OngoingPoll extends Component{
    state={
        questionLength: '0',
        questions: []

    };
    _isMounted= true;
    async componentDidMount(){
        const questionLength = await poll.methods.getQuestionLength().call();

        const questions = await Promise.all(
            Array(parseInt(questionLength))
            .fill()
            .map((element, index) => {
                return poll.methods.questions(index).call()
            })
        );
        if(this._isMounted){
            this.setState({
                questionLength,
                questions
            });
        }

    }
    componentWillUnmount(){
        this._isMounted = false;
    }
    renderQuestion(){
        const items = this.state.questions.map(question =>{
                return{
                    header: question[0],
                    content: 
                    <Button>Yay</Button>
                };
            });

        return <Card.Group items ={items}/> 
    }


    render(){
        return(
            <Layout>
                {this.state.questionLength}     
                {this.renderQuestion()}           
                    <Button primary>Ask a question</Button>

            </Layout>
        );
    }
}

export default OngoingPoll;
