import React, {Component} from 'react';
import Header from './Header';
import {Container,Grid, Menu, Segment, Button} from 'semantic-ui-react';
import Head from 'next/head';
import {Link} from '../routes';

export default class Layout extends Component{
    state={activeItem: 'home'}
    itemClick = (e, {name})=> this.setState
    render(){
        const { activeItem } = this.setState({ activeItem: name })
        return(
            <div>
                <Head>
                    <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
                </Head>
                <Segment inverted textAlign='center' style={{minHeight: 700, padding: '1em 0em'}} vertical>
                    <Menu fixed inverted pointing secondary size='large'>
                        <Container>
                            <Link route="/ongoing">
                                <Menu.Item as='a'
                                    name='ongoing'
                                    active={activeItem === 'ongoing'}
                                    onClick={this.itemClick}>
                                    Ongoing Polls
                                </Menu.Item>
                            </Link>
                            <Link route="/results">
                                <Menu.Item as='a'    
                                    name='ongoing'
                                    active={activeItem === 'ongoing'}
                                    onClick={this.itemClick}>
                                    Results
                                </Menu.Item>
                            </Link>
                            <Link route="/">
                                <Menu.Item as='a' 
                                    position='right'
                                    name='ongoing'
                                    active={activeItem === 'ongoing'}
                                    onClick={this.itemClick}>
                                    Manager
                                </Menu.Item>
                            </Link>    
                        </Container>
                    </Menu>
                    {props.children}
                </Segment>
            </div>
        );
    };
}
