import React, {Component} from 'react';
import manager from '../ethereum/manager';
import { Card, Button } from 'semantic-ui-react';
import Layout from '../components/Layout';

class PollIndex extends Component {
    static async getInitialProps(){
        const polls = await manager.methods.getDeployedPolls().call();
        return {polls};
    }

    renderPolls(){
        const items=this.props.polls.map(address=>{
            return {
                header: 'This Poll is managed by',
                description: address,
                fluid:true
            };
        });
        return <Card.Group items={items} />
    }
    render(){
        return (
            <Layout>
                {this.renderPolls()}
                <Button primary style={{align:'center'}}>Answer Questions</Button>
            </Layout>


        );
    }
}

export default PollIndex;
