import React from 'react';
import Header from './Header';
import {Container,Grid, Menu, Segment} from 'semantic-ui-react';
import Head from 'next/head';


export default props => {
    return(
        <div>
            <Head>
                <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
            </Head>

            <Header />
            <Grid >
            <Grid.Column width={3} style={{marginTop: '50px'}}>
                <Menu style={{height: '80vh'}} fluid inverted vertical>
                    <Menu.Item as= 'a'> Open Polls </Menu.Item>
                    <Menu.Item as ='a'> Results </Menu.Item>
                </Menu>
                <Menu fluid inverted vertical>
                    <Menu.Item as='a' position='right'> Manager</Menu.Item>
                </Menu>
            </Grid.Column>
            <Grid.Column stretched width={12}>
                <Segment placeholder style={{marginTop: '30px'}}>
                    {props.children}
                </Segment>
            </Grid.Column>
        </Grid>
        </div>
    );
};
