import React, {Component} from 'react';
import manager from '../ethereum/manager';
import { Card, Button, Container, Item } from 'semantic-ui-react';
import Layout from '../components/Layout';

class PollIndex extends Component {
    static async getInitialProps(){
        const polls = await manager.methods.getDeployedPolls().call();
        return {polls};
    }

    renderPolls(){
        const items=this.props.polls.map(address=>{
            return {
                header: 'This Poll is managed by',
                description: address,
                fluid:true
            };
        });
        return <Item.Group items={items} />
    }
    render(){
        return (
            <Layout>
                {this.renderPolls()}
            </Layout>


        );
    }
}

export default PollIndex;
