import React, {Component} from 'react';
import Layout from '../components/Layout';
import {Button, Card, Container} from 'semantic-ui-react';
import poll from '../ethereum/poll';
import {Link} from '../routes'
import { AsyncResource } from 'async_hooks';
import web3 from '../ethereum/web3';
class OngoingPoll extends Component{
    static async getInitialProps(){
        const questionArray = await poll.methods.questions().call();
        console.log(questionArray);
        return{
            questionsArray
        }
    }
    state={
        questionLength: '0',
        questions: [],
    };
    _isMounted= true;
    async componentDidMount(){
        const questionLength = await poll.methods.getQuestionLength().call();

        const questions = await Promise.all(
            Array(parseInt(questionLength))
            .fill()
            .map((element, index) => {
                return poll.methods.questions(index).call()
            })
        );

            this.setState({
                questionLength,
                questions
            });


    }

    voteYay = () => {
        console.log("votes");
    }

    voteNay(){

    }
    renderQuestion(){
        const items = this.state.questions.map((index,question) =>{
                return{
                    id: {index},
                    header: question[0],
                    description: (
                        <div>
                            <Button onClick={this.voteYay}>Yay</Button>
                            <Button>Nay</Button>
                        </div>
                    )
                };
            });

        return <Card.Group centered itemsPerRow={2} items ={items}/> 
    }


    render(){
        return(
            <Layout>
                <Container>
                    <p>{this.state.questionLength} Ongoing Polls</p>
                    {this.renderQuestion()}       
                </Container>
                <Link route='/ongoing/add'>
                <Button primary style={{marginTop: '10px'}}>Ask a question</Button>
                </Link>

            </Layout>
        );
    }
}

export default OngoingPoll;
